mapper.select:
select *, date_format(regdate, '%Y-%m-%d %T') fmtdate, format(price, 0) fmtprice 
from market where title like concat('%', #{query}, '%')
order by sid desc limit #{start}, #{size}

select *, date_format(regdate, '%Y-%m-%d %T') fmtdate, format(price, 0) fmtprice,
(select count(*) from mcomments where uid=#{uid} and sid=#{sid})
form market where sid=#{sid}
